def trouver_ecoles_par_domaine(domaine: str):
    """
    Retourne jusqu'à 3 établissements (publics + privés) correspondant au domaine.
    Recherche insensible à la casse et partielle.
    """
    domaine_lower = domaine.lower()
    result = []
    
    all_schools = UNIVERSITES_PUBLIQUES + UNIVERSITES_PRIVEES_PRINCIPALES
    
    for school in all_schools:
        if any(domaine_lower in d.lower() for d in school["domaines"]):
            result.append(school)
    
    return result[:3]
